import React, { Component, RefObject, createRef } from 'react';
import { render } from 'react-dom';
import './Flower.css';
import { TweenLite } from "gsap";

interface AppProps {}
interface AppState {}

export class Flower extends Component <AppProps, AppState> {

  constructor(props: AppProps) {
    super(props);

    this.box = null;
    this.t = null;
  }

  box: HTMLDivElement | null;
  t: ReturnType<typeof TweenLite.to> | null;

  componentDidMount() {
    this.t = TweenLite.to(this.box, 1, {x: 200, rotation: 360});
  }

  render () {
    return (
      <div>
        <div className='papaDiv'>
          <div className='bgDiv'>
            <img className='bgImg fImgs' src='./flower/background.png' alt='sky above a plot of earth' />
          </div>
          <div className='cloudDiv'>
            <img className='cloudImg fImgs' src='./flower/cloud.png' alt='cloud' />
          </div>
          <div className='waterDiv'>
            <img className='waterImg fImgs' src='./flower/waterdrop.png' alt='drop of water' />
          </div>
        <button
          onClick={() => t.restart()}
        >Restart</button>
        <div className="box"
          ref={e => this.box = e}
        ></div>
        </div>

      </div>
    )
  }

}

render(<Flower />, document.getElementById('root'));
